import React, { useState, useEffect } from 'react';
import axios from 'axios';

function UserProfile() {
  const [user, setUser] = useState({
    name: '', 
    email: '', 
    phone: '', 
    profilePic: '', 
  });
  const [editing, setEditing] = useState(false);
  const [orders, setOrders] = useState([]);
  const [image, setImage] = useState(null);
  const [preview, setPreview] = useState(null);

  // Retrieve email from localStorage
  const email = localStorage.getItem('email'); 

  useEffect(() => {
    if (email) {
      axios.get(`https://food-delivery-gj0r.onrender.com/api/users/${email}`)
        .then((res) => {
          setUser(res.data);
        })
        .catch(console.error);
    }
  }, [email]);

  const handleEdit = () => setEditing(true);

  const handleSave = () => {
    axios.put('https://food-delivery-gj0r.onrender.com/api/users', user)
      .then((res) => {
        setUser(res.data);
        setEditing(false);
      })
      .catch(console.error);
  };

  const handlePictureChange = () => {
    if (!image) return;

    const formData = new FormData();
    formData.append('email', user.email);
    formData.append('profilePic', image);

    axios.post('https://food-delivery-gj0r.onrender.com/api/users/upload', formData)
      .then((res) => {
        setUser(res.data);
        setPreview(null);
        setImage(null);
      })
      .catch(console.error);
  };

  const fetchOrders = () => {
    axios.get(`https://food-delivery-gj0r.onrender.com/api/orders/${email}`)
      .then((res) => setOrders(res.data))
      .catch(console.error);
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    setImage(file);
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => setPreview(reader.result);
      reader.readAsDataURL(file);
    } else {
      setPreview(null);
    }
  };

  const handleLogout = () => {
    alert('Logged out successfully!');
  };

  return (
    <div className="container mt-5" style={{
      maxWidth: '800px', margin: '0 auto', padding: '20px', borderRadius: '8px',
      backgroundColor: '#f4f4f9', boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
    }}>
      <div className="card shadow-lg p-4" style={{
        backgroundColor: '#fff', borderRadius: '10px', padding: '20px', textAlign: 'center'
      }}>
        <div className="text-center mb-3">
          <img
            src={preview ? preview : user.profilePic ? `https://food-delivery-gj0r.onrender.com${user.profilePic}` : 'https://via.placeholder.com/120?text=User'}
            alt="Profile"
            className="rounded-circle mb-2"
            width="120"
            height="120"
            style={{
              objectFit: 'cover', border: '5px solid #6f42c1', boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)', marginBottom: '20px',
            }}
          />
          <h4 className="mb-3" style={{ color: '#6f42c1', fontWeight: '600' }}>{user.name || 'Your Name'}</h4>
          <div className="mb-3">
            <input
              type="file"
              accept="image/*"
              className="form-control"
              style={{
                marginBottom: '10px', borderRadius: '8px', border: '1px solid #ddd', fontSize: '14px', padding: '8px',
              }}
              onChange={handleImageChange}
            />
            <button
              className="btn btn-link text-primary mt-2"
              onClick={handlePictureChange}
              disabled={!image}
              style={{
                backgroundColor: 'transparent', fontWeight: '600', fontSize: '16px', cursor: 'pointer', 
              }}
            >
              {image ? 'Upload Picture' : 'Choose Picture'}
            </button>
          </div>
        </div>

        <div className="mb-3">
          <label className="form-label" style={{ fontWeight: '600', fontSize: '16px' }}>Name</label>
          <input
            type="text"
            className="form-control"
            value={user.name || ''}
            disabled={!editing}
            onChange={(e) => setUser({ ...user, name: e.target.value })}
            style={{
              borderRadius: '8px', padding: '10px', fontSize: '14px', borderColor: '#ddd', boxShadow: 'none', 
            }}
          />
        </div>

        <div className="mb-3">
          <label className="form-label" style={{ fontWeight: '600', fontSize: '16px' }}>Email</label>
          <input
            type="email"
            className="form-control"
            value={user.email || ''}
            disabled={!editing}
            onChange={(e) => setUser({ ...user, email: e.target.value })}
            style={{
              borderRadius: '8px', padding: '10px', fontSize: '14px', borderColor: '#ddd', boxShadow: 'none',
            }}
          />
        </div>

        <div className="mb-4">
          <label className="form-label" style={{ fontWeight: '600', fontSize: '16px' }}>Phone</label>
          <input
            type="text"
            className="form-control"
            value={user.phone || ''}
            disabled={!editing}
            onChange={(e) => setUser({ ...user, phone: e.target.value })}
            style={{
              borderRadius: '8px', padding: '10px', fontSize: '14px', borderColor: '#ddd', boxShadow: 'none',
            }}
          />
        </div>

        <div className="d-flex justify-content-between mb-3" style={{ fontSize: '16px' }}>
          {!editing ? (
            <button className="btn btn-primary" onClick={handleEdit} style={{
              backgroundColor: '#6f42c1', borderRadius: '8px', padding: '10px 20px', color: '#fff', cursor: 'pointer',
            }}>Edit</button>
          ) : (
            <button className="btn btn-success" onClick={handleSave} style={{
              backgroundColor: '#28a745', borderRadius: '8px', padding: '10px 20px', color: '#fff', cursor: 'pointer',
            }}>Save</button>
          )}

          <button className="btn btn-info text-white" onClick={fetchOrders} style={{
            backgroundColor: '#17a2b8', borderRadius: '8px', padding: '10px 20px', color: '#fff', cursor: 'pointer',
          }}>See Orders</button>

          <button className="btn btn-danger" onClick={handleLogout} style={{
            backgroundColor: '#dc3545', borderRadius: '8px', padding: '10px 20px', color: '#fff', cursor: 'pointer',
          }}>Logout</button>
        </div>

        {orders.length > 0 && (
          <div className="mt-4">
            <h5 className="mb-3" style={{ color: '#6f42c1', fontWeight: '600' }}>Past Orders</h5>
            <ul className="list-group" style={{ listStyleType: 'none', paddingLeft: '0' }}>
              {orders.map((order, i) => (
                <li key={i} className="list-group-item" style={{
                  backgroundColor: '#f8f9fa', padding: '10px', borderRadius: '8px', marginBottom: '10px',
                }}>
                  <strong>{order.items.join(', ')}</strong> – {new Date(order.date).toLocaleDateString()}
                </li>
              ))}
            </ul>
          </div>
        )}
      </div>
    </div>
  );
}

export default UserProfile;
