import React, { useState, useEffect } from 'react';
import axios from 'axios';

function UserProfile() {
  const [user, setUser] = useState({ name: '', email: '', phone: '', profilePic: '' });
  const [editing, setEditing] = useState(false);
  const [orders, setOrders] = useState([]);
  const [image, setImage] = useState(null);
  const [preview, setPreview] = useState(null);

  // ðŸ”‘ Get logged-in user email from localStorage
  const email = localStorage.getItem('userEmail');

  useEffect(() => {
    if (email) {
      axios.get(`https://food-delivery-gj0r.onrender.com/api/user/${email}`)
        .then(res => setUser(res.data))
        .catch(console.error);
    }
  }, [email]);

  const handleEdit = () => setEditing(true);

  const handleSave = () => {
    axios.put('https://food-delivery-gj0r.onrender.com/api/user', user)
      .then(res => {
        setUser(res.data);
        setEditing(false);
      })
      .catch(console.error);
  };

  const handlePictureChange = () => {
    if (!image) return;

    const formData = new FormData();
    formData.append('email', user.email);
    formData.append('profilePic', image);

    axios.post('https://food-delivery-gj0r.onrender.com/api/user/upload', formData)
      .then(res => {
        setUser(res.data);
        setPreview(null);
        setImage(null);
      })
      .catch(console.error);
  };

  const fetchOrders = () => {
    axios.get(`https://food-delivery-gj0r.onrender.com/api/orders/${email}`)
      .then(res => setOrders(res.data))
      .catch(console.error);
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    setImage(file);
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => setPreview(reader.result);
      reader.readAsDataURL(file);
    } else {
      setPreview(null);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('userEmail');
    alert('Logged out successfully!');
    // Redirect to login page
    window.location.href = '/login';
  };

  const containerStyle = {
    maxWidth: '600px',
    margin: '50px auto',
    padding: '30px',
    background: '#fff',
    borderRadius: '12px',
    boxShadow: '0 4px 16px rgba(0, 0, 0, 0.1)'
  };

  const labelStyle = {
    fontWeight: '500',
    color: '#333',
    marginBottom: '5px'
  };

  const inputStyle = {
    borderRadius: '8px',
    padding: '10px',
    fontSize: '16px',
    marginBottom: '20px',
    width: '100%',
    border: '1px solid #ccc'
  };

  const buttonStyle = {
    padding: '10px 20px',
    borderRadius: '6px',
    border: 'none',
    fontSize: '14px',
    fontWeight: '500',
    cursor: 'pointer'
  };

  const btnPrimary = { ...buttonStyle, backgroundColor: '#007bff', color: '#fff' };
  const btnSuccess = { ...buttonStyle, backgroundColor: '#28a745', color: '#fff' };
  const btnInfo = { ...buttonStyle, backgroundColor: '#17a2b8', color: '#fff' };
  const btnDanger = { ...buttonStyle, backgroundColor: '#dc3545', color: '#fff' };
  const btnLink = { ...buttonStyle, backgroundColor: 'transparent', color: '#007bff' };

  return (
    <div style={containerStyle}>
      <div style={{ textAlign: 'center', marginBottom: '20px' }}>
        <img
          src={
            preview
              ? preview
              : user.profilePic
              ? `https://food-delivery-gj0r.onrender.com${user.profilePic}`
              : 'https://via.placeholder.com/120?text=User'
          }
          alt="Profile"
          style={{
            width: '120px',
            height: '120px',
            borderRadius: '50%',
            objectFit: 'cover',
            border: '3px solid #6f42c1',
            marginBottom: '10px'
          }}
        />
        <h4 style={{ marginBottom: '10px' }}>{user.name || 'Your Name'}</h4>
        <input
          type="file"
          accept="image/*"
          onChange={handleImageChange}
          style={{ marginBottom: '10px' }}
        />
        <div>
          <button
            style={btnLink}
            onClick={handlePictureChange}
            disabled={!image}
          >
            {image ? 'Upload Picture' : 'Choose Picture'}
          </button>
        </div>
      </div>

      <div>
        <label style={labelStyle}>Name</label>
        <input
          type="text"
          value={user.name}
          disabled={!editing}
          style={inputStyle}
          onChange={(e) => setUser({ ...user, name: e.target.value })}
        />
      </div>

      <div>
        <label style={labelStyle}>Email</label>
        <input
          type="email"
          value={user.email}
          disabled={true}
          style={inputStyle}
        />
      </div>

      <div>
        <label style={labelStyle}>Phone</label>
        <input
          type="text"
          value={user.phone}
          disabled={!editing}
          style={inputStyle}
          onChange={(e) => setUser({ ...user, phone: e.target.value })}
        />
      </div>

      <div style={{ display: 'flex', justifyContent: 'space-between', marginTop: '10px' }}>
        {!editing ? (
          <button style={btnPrimary} onClick={handleEdit}>Edit</button>
        ) : (
          <button style={btnSuccess} onClick={handleSave}>Save</button>
        )}
        <button style={btnInfo} onClick={fetchOrders}>See Orders</button>
        <button style={btnDanger} onClick={handleLogout}>Logout</button>
      </div>

      {orders.length > 0 && (
        <div style={{ marginTop: '30px' }}>
          <h5 style={{ fontWeight: '600' }}>Past Orders</h5>
          <ul style={{ paddingLeft: '20px' }}>
            {orders.map((order, i) => (
              <li key={i} style={{ marginBottom: '10px' }}>
                <strong>{order.items.join(', ')}</strong> â€“ {new Date(order.date).toLocaleDateString()}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default UserProfile;
