import React, { useState, useEffect } from 'react';
import axios from 'axios';

function UserProfile() {
  const [user, setUser] = useState({ name: '', email: '', phone: '', profilePic: '' });
  const [editing, setEditing] = useState(false);
  const [orders, setOrders] = useState([]);
  const [image, setImage] = useState(null);
  const [preview, setPreview] = useState(null);

  const userInfo = JSON.parse(localStorage.getItem('user'));
  const email = userInfo ? userInfo.email : '';

  useEffect(() => {
    if (email) {
      axios.get(`https://food-delivery-gj0r.onrender.com/api/users/${email}`)
        .then(res => setUser(res.data))
        .catch(console.error);
    }
  }, [email]);

  const handleEdit = () => setEditing(true);
  const handleSave = () => {
    axios.put('https://food-delivery-gj0r.onrender.com/api/users', user)
      .then(res => {
        setUser(res.data);
        setEditing(false);
      })
      .catch(console.error);
  };

  const handlePictureChange = () => {
    if (!image) return;
    const formData = new FormData();
    formData.append('email', user.email);
    formData.append('profilePic', image);
    axios.post('https://food-delivery-gj0r.onrender.com/api/users/upload', formData)
      .then(res => {
        setUser(res.data);
        setPreview(null);
        setImage(null);
      })
      .catch(console.error);
  };

  const fetchOrders = () => {
    axios.get(`https://food-delivery-gj0r.onrender.com/api/orders/${email}`)
      .then(res => setOrders(res.data))
      .catch(console.error);
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    setImage(file);
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => setPreview(reader.result);
      reader.readAsDataURL(file);
    } else {
      setPreview(null);
    }
  };

  const handleLogout = () => {
    alert('Logged out successfully!');
    localStorage.removeItem('user');
  };

  return (
    <div style={styles.wrapper}>
      <div style={styles.profileCard}>
        <div style={styles.leftSection}>
          <img
            src={preview ? preview : user.profilePic ? `https://food-delivery-gj0r.onrender.com${user.profilePic}` : 'https://via.placeholder.com/120?text=User'}
            alt="User"
            style={styles.profileImage}
          />
          <input type="file" accept="image/*" onChange={handleImageChange} style={{ display: 'none' }} id="upload" />
          <label htmlFor="upload" style={styles.uploadLabel}>Choose Picture</label>
          {image && (
            <button style={styles.uploadBtn} onClick={handlePictureChange}>Upload</button>
          )}
          <h2 style={styles.userName}>{user.name || 'Your Name'}</h2>
          <p style={styles.userEmail}>{user.email}</p>
        </div>

        <div style={styles.rightSection}>
          <div style={styles.fieldGroup}>
            <label>Name</label>
            <input
              type="text"
              disabled={!editing}
              value={user.name}
              onChange={(e) => setUser({ ...user, name: e.target.value })}
              style={styles.input}
            />
          </div>

          <div style={styles.fieldGroup}>
            <label>Email</label>
            <input
              type="email"
              disabled={!editing}
              value={user.email}
              onChange={(e) => setUser({ ...user, email: e.target.value })}
              style={styles.input}
            />
          </div>

          <div style={styles.fieldGroup}>
            <label>Phone</label>
            <input
              type="text"
              disabled={!editing}
              value={user.phone}
              onChange={(e) => setUser({ ...user, phone: e.target.value })}
              style={styles.input}
            />
          </div>

          <div style={styles.buttonContainer}>
            {!editing ? (
              <button style={styles.editBtn} onClick={handleEdit}>Edit</button>
            ) : (
              <button style={styles.saveBtn} onClick={handleSave}>Save</button>
            )}
            <button style={styles.orderBtn} onClick={fetchOrders}>See Orders</button>
            <button style={styles.logoutBtn} onClick={handleLogout}>Logout</button>
          </div>

          {orders.length > 0 && (
            <div style={styles.orderSection}>
              <h3>Order History</h3>
              <ul style={styles.orderList}>
                {orders.map((order, i) => (
                  <li key={i}>{order.items.join(', ')} - {new Date(order.date).toLocaleDateString()}</li>
                ))}
              </ul>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

const styles = {
  wrapper: {
    display: 'flex',
    justifyContent: 'center',
    padding: '50px 20px',
    background: '#f4f4f4',
  },
  profileCard: {
    display: 'flex',
    background: '#fff',
    borderRadius: '15px',
    boxShadow: '0 0 20px rgba(0,0,0,0.1)',
    width: '100%',
    maxWidth: '1000px',
    overflow: 'hidden',
  },
  leftSection: {
    background: '#6f42c1',
    color: '#fff',
    padding: '30px 20px',
    textAlign: 'center',
    flex: '1',
  },
  rightSection: {
    flex: '2',
    padding: '30px',
  },
  profileImage: {
    width: '120px',
    height: '120px',
    borderRadius: '50%',
    objectFit: 'cover',
    border: '4px solid #fff',
    marginBottom: '10px',
  },
  uploadLabel: {
    display: 'inline-block',
    background: '#fff',
    color: '#6f42c1',
    padding: '5px 10px',
    borderRadius: '5px',
    fontWeight: 'bold',
    cursor: 'pointer',
    marginBottom: '10px',
  },
  uploadBtn: {
    background: '#ffffff90',
    color: '#6f42c1',
    padding: '5px 10px',
    borderRadius: '5px',
    border: 'none',
    marginBottom: '10px',
    cursor: 'pointer',
  },
  userName: {
    fontSize: '24px',
    fontWeight: 'bold',
    marginTop: '10px',
  },
  userEmail: {
    fontSize: '14px',
    color: '#eee',
  },
  fieldGroup: {
    marginBottom: '20px',
  },
  input: {
    width: '100%',
    padding: '10px',
    fontSize: '16px',
    borderRadius: '5px',
    border: '1px solid #ccc',
    marginTop: '5px',
  },
  buttonContainer: {
    display: 'flex',
    gap: '15px',
    flexWrap: 'wrap',
    marginTop: '20px',
  },
  editBtn: {
    background: '#28a745',
    color: '#fff',
    border: 'none',
    padding: '10px 20px',
    borderRadius: '5px',
    cursor: 'pointer',
  },
  saveBtn: {
    background: '#007bff',
    color: '#fff',
    border: 'none',
    padding: '10px 20px',
    borderRadius: '5px',
    cursor: 'pointer',
  },
  orderBtn: {
    background: '#17a2b8',
    color: '#fff',
    border: 'none',
    padding: '10px 20px',
    borderRadius: '5px',
    cursor: 'pointer',
  },
  logoutBtn: {
    background: '#dc3545',
    color: '#fff',
    border: 'none',
    padding: '10px 20px',
    borderRadius: '5px',
    cursor: 'pointer',
  },
  orderSection: {
    marginTop: '30px',
  },
  orderList: {
    listStyle: 'none',
    padding: '0',
    marginTop: '10px',
  },
};

export default UserProfile;
