import React, { useState, useEffect } from 'react';
import axios from 'axios';

function UserProfile() {
  const [user, setUser] = useState({ name: '', email: '', phone: '', profilePic: '' });
  const [editing, setEditing] = useState(false);
  const [orders, setOrders] = useState([]);
  const [image, setImage] = useState(null);
  const [preview, setPreview] = useState(null);

  const userInfo = JSON.parse(localStorage.getItem('user')); // Get user info from localStorage
  const email = userInfo ? userInfo.email : ''; // Assuming email is stored in localStorage

  useEffect(() => {
    if (email) {
      axios.get(`https://food-delivery-gj0r.onrender.com/api/users/${email}`)
        .then(res => setUser(res.data))
        .catch(console.error);
    }
  }, [email]);

  const handleEdit = () => setEditing(true);

  const handleSave = () => {
    axios.put('https://food-delivery-gj0r.onrender.com/api/users', user)
      .then(res => {
        setUser(res.data);
        setEditing(false);
      })
      .catch(console.error);
  };

  const handlePictureChange = () => {
    if (!image) return;

    const formData = new FormData();
    formData.append('email', user.email);
    formData.append('profilePic', image);

    axios.post('https://food-delivery-gj0r.onrender.com/api/users/upload', formData)
      .then(res => {
        setUser(res.data);
        setPreview(null);
        setImage(null);
      })
      .catch(console.error);
  };

  const fetchOrders = () => {
    axios.get(`https://food-delivery-gj0r.onrender.com/api/orders/${email}`)
      .then(res => setOrders(res.data))
      .catch(console.error);
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    setImage(file);
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => setPreview(reader.result);
      reader.readAsDataURL(file);
    } else {
      setPreview(null);
    }
  };

  const handleLogout = () => {
    alert('Logged out successfully!');
    localStorage.removeItem('user'); // Clear localStorage
    // Redirect to login page (if needed)
  };

  return (
    <div style={{ marginTop: '50px', display: 'flex', justifyContent: 'center' }}>
      <div style={{ padding: '20px', backgroundColor: '#fff', borderRadius: '10px', boxShadow: '0px 4px 10px rgba(0, 0, 0, 0.1)', width: '100%', maxWidth: '600px' }}>
        <div style={{ textAlign: 'center' }}>
          <img
            src={preview ? preview : user.profilePic ? `https://food-delivery-gj0r.onrender.com${user.profilePic}` : 'https://via.placeholder.com/120?text=User'}
            alt="Profile"
            style={{ borderRadius: '50%', marginBottom: '15px', width: '120px', height: '120px', objectFit: 'cover', border: '3px solid #6f42c1' }}
          />
          <h4 style={{ fontWeight: 'bold', fontSize: '24px' }}>{user.name || 'Your Name'}</h4>

          <div>
            <input
              type="file"
              accept="image/*"
              style={{ marginTop: '10px' }}
              onChange={handleImageChange}
            />
            <button
              onClick={handlePictureChange}
              disabled={!image}
              style={{ marginTop: '10px', backgroundColor: '#6f42c1', color: '#fff', border: 'none', padding: '8px 15px', borderRadius: '5px' }}
            >
              {image ? 'Upload Picture' : 'Choose Picture'}
            </button>
          </div>
        </div>

        <div style={{ marginTop: '20px' }}>
          <div style={{ marginBottom: '15px' }}>
            <label style={{ display: 'block', marginBottom: '5px' }}>Name</label>
            <input
              type="text"
              value={user.name || ''}
              disabled={!editing}
              onChange={(e) => setUser({ ...user, name: e.target.value })}
              style={{ width: '100%', padding: '10px', borderRadius: '5px', border: '1px solid #ccc',color:'black' }}
            />
          </div>

          <div style={{ marginBottom: '15px' }}>
            <label style={{ display: 'block', marginBottom: '5px' }}>Email</label>
            <input
              type="email"
              value={user.email || ''}
              disabled={!editing}
              onChange={(e) => setUser({ ...user, email: e.target.value })}
              style={{ width: '100%', padding: '10px', borderRadius: '5px', border: '1px solid #ccc', color:'black' }}
            />
          </div>

          <div style={{ marginBottom: '15px' }}>
            <label style={{ display: 'block', marginBottom: '5px' }}>Phone</label>
            <input
              type="text"
              value={user.phone || ''}
              disabled={!editing}
              onChange={(e) => setUser({ ...user, phone: e.target.value })}
              style={{ width: '100%', padding: '10px', borderRadius: '5px', border: '1px solid #ccc' }}
            />
          </div>

          <div style={{ display: 'flex', justifyContent: 'space-between' }}>
            {!editing ? (
              <button
                onClick={handleEdit}
                style={{ backgroundColor: '#28a745', color: '#fff', padding: '8px 15px', borderRadius: '5px', border: 'none' }}
              >
                Edit
              </button>
            ) : (
              <button
                onClick={handleSave}
                style={{ backgroundColor: '#007bff', color: '#fff', padding: '8px 15px', borderRadius: '5px', border: 'none' }}
              >
                Save
              </button>
            )}

            <button
              onClick={fetchOrders}
              style={{ backgroundColor: '#17a2b8', color: '#fff', padding: '8px 15px', borderRadius: '5px', border: 'none' }}
            >
              See Orders
            </button>
            <button
              onClick={handleLogout}
              style={{ backgroundColor: '#dc3545', color: '#fff', padding: '8px 15px', borderRadius: '5px', border: 'none' }}
            >
              Logout
            </button>
          </div>
        </div>

        {orders.length > 0 && (
          <div style={{ marginTop: '20px' }}>
            <h5>Past Orders</h5>
            <ul style={{ listStyle: 'none', padding: '0' }}>
              {orders.map((order, i) => (
                <li key={i} style={{ padding: '10px', borderBottom: '1px solid #ccc' }}>
                  <strong>{order.items.join(', ')}</strong> – {new Date(order.date).toLocaleDateString()}
                </li>
              ))}
            </ul>
          </div>
        )}
      </div>
    </div>
  );
}

export default UserProfile;
