import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { ToastContainer } from 'react-toastify';
import { notifySuccess, notifyError } from '../utils/utils';
import 'react-toastify/dist/ReactToastify.css';

function Login() {
    const [loginInfo, setLoginInfo] = useState({ email: '', password: '' });
    const navigate = useNavigate();

    const handleChange = (e) => {
        setLoginInfo({ ...loginInfo, [e.target.name]: e.target.value });
    };

    const handleLogin = async (e) => {
        e.preventDefault();
        const { email, password } = loginInfo;
        if (!email || !password) return notifyError('Email and password are required');

        try {
            const res = await fetch("https://food-delivery-gj0r.onrender.com/api/users/login", {
                method: "POST",
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(loginInfo)
            });
            const result = await res.json();

            if (result.success) {
                notifySuccess(result.message);
                localStorage.setItem('token', result.jwtToken);
                localStorage.setItem('loggedInUser', result.name);
                setTimeout(() => navigate('/home'), 1000);
            } else {
                notifyError(result?.error?.details?.[0]?.message || result.message);
            }
        } catch (err) {
            notifyError('Something went wrong!');
        }
    };

    return (
        <div className='container'>
            <h1>Login</h1>
            <form onSubmit={handleLogin}>
                <div>
                    <label>Email</label>
                    <input type='email' name='email' placeholder='Enter your email' value={loginInfo.email} onChange={handleChange} />
                </div>
                <div>
                    <label>Password</label>
                    <input type='password' name='password' placeholder='Enter your password' value={loginInfo.password} onChange={handleChange} />
                </div>
                <button type='submit'>Login</button>
                <p>Don't have an account? <Link to="/signup">Signup</Link></p>
            </form>
            <ToastContainer />
        </div>
    );
}

export default Login;
