import React, { useState, useEffect } from 'react';
import axios from 'axios';

function UserProfile() {
  const [user, setUser] = useState({ name: '', email: '', phone: '', profilePic: '' });
  const [editing, setEditing] = useState(false);
  const [orders, setOrders] = useState([]);
  const [image, setImage] = useState(null);
  const [preview, setPreview] = useState(null);

  const userFromStorage = JSON.parse(localStorage.getItem('user'));
  const email = userFromStorage?.email;

  useEffect(() => {
    if (email) {
      axios.get(`https://food-delivery-gj0r.onrender.com/api/users/${email}`)
        .then(res => setUser(res.data))
        .catch(console.error);
    }
  }, [email]);

  const handleEdit = () => setEditing(true);

  const handleSave = () => {
    axios.put('https://food-delivery-gj0r.onrender.com/api/users', user)
      .then(res => {
        setUser(res.data);
        setEditing(false);
      })
      .catch(console.error);
  };

  const handlePictureChange = () => {
    if (!image) return;

    const formData = new FormData();
    formData.append('email', user.email);
    formData.append('profilePic', image);

    axios.post('https://food-delivery-gj0r.onrender.com/api/users/upload', formData)
      .then(res => {
        setUser(res.data);
        setPreview(null);
        setImage(null);
      })
      .catch(console.error);
  };

  const fetchOrders = () => {
    axios.get(`https://food-delivery-gj0r.onrender.com/api/orders/${email}`)
      .then(res => setOrders(res.data))
      .catch(console.error);
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    setImage(file);
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => setPreview(reader.result);
      reader.readAsDataURL(file);
    } else {
      setPreview(null);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('user');
    window.location.href = '/login';
  };

  return (
    <div style={{ maxWidth: '600px', margin: '40px auto', padding: '30px', boxShadow: '0 0 15px rgba(0,0,0,0.1)', borderRadius: '10px', backgroundColor: '#fff' }}>
      <div style={{ textAlign: 'center' }}>
        <img
          src={
            preview
              ? preview
              : user.profilePic
              ? `https://food-delivery-gj0r.onrender.com${user.profilePic}`
              : 'https://via.placeholder.com/120?text=User'
          }
          alt="Profile"
          style={{ borderRadius: '50%', width: '120px', height: '120px', objectFit: 'cover', border: '3px solid #6f42c1' }}
        />
        <h2 style={{ marginTop: '10px' }}>{user.name || 'Your Name'}</h2>
        <input type="file" accept="image/*" onChange={handleImageChange} />
        <button onClick={handlePictureChange} disabled={!image} style={{ background: 'none', color: '#007bff', border: 'none', marginTop: '10px', cursor: 'pointer' }}>
          {image ? 'Upload Picture' : 'Choose Picture'}
        </button>
      </div>

      <div style={{ marginTop: '30px' }}>
        <label>Name</label>
        <input
          type="text"
          value={user.name}
          disabled={!editing}
          onChange={(e) => setUser({ ...user, name: e.target.value })}
          style={inputStyle}
        />

        <label style={{ marginTop: '15px' }}>Email</label>
        <input
          type="email"
          value={user.email}
          disabled
          style={inputStyle}
        />

        <label style={{ marginTop: '15px' }}>Phone</label>
        <input
          type="text"
          value={user.phone}
          disabled={!editing}
          onChange={(e) => setUser({ ...user, phone: e.target.value })}
          style={inputStyle}
        />
      </div>

      <div style={{ display: 'flex', justifyContent: 'space-between', marginTop: '25px' }}>
        {!editing ? (
          <button style={buttonStyle('#007bff')} onClick={handleEdit}>Edit</button>
        ) : (
          <button style={buttonStyle('#28a745')} onClick={handleSave}>Save</button>
        )}
        <button style={buttonStyle('#17a2b8')} onClick={fetchOrders}>See Orders</button>
        <button style={buttonStyle('#dc3545')} onClick={handleLogout}>Logout</button>
      </div>

      {orders.length > 0 && (
        <div style={{ marginTop: '30px' }}>
          <h4>Past Orders</h4>
          <ul style={{ listStyle: 'none', paddingLeft: 0 }}>
            {orders.map((order, i) => (
              <li key={i} style={{ padding: '10px', borderBottom: '1px solid #ccc' }}>
                <strong>{order.items.map(i => i.foodItem).join(', ')}</strong> – {new Date(order.createdAt).toLocaleDateString()}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

const inputStyle = {
  width: '100%',
  padding: '10px',
  marginTop: '5px',
  border: '1px solid #ccc',
  borderRadius: '5px',
  fontSize: '16px'
};

const buttonStyle = (bgColor) => ({
  backgroundColor: bgColor,
  border: 'none',
  padding: '10px 20px',
  color: '#fff',
  borderRadius: '5px',
  cursor: 'pointer'
});

export default UserProfile;
