import React, { useState, useEffect } from 'react';
import axios from 'axios';

function UserProfile() {
  const [user, setUser] = useState({ name: '', email: '', phone: '', profilePic: '' });
  const [editing, setEditing] = useState(false);
  const [orders, setOrders] = useState([]);
  const [image, setImage] = useState(null);
  const [preview, setPreview] = useState(null);

  const storedUser = JSON.parse(localStorage.getItem('user'));
  const email = storedUser?.email;

  useEffect(() => {
    if (!email) return;

    const fetchUser = async () => {
      try {
        const res = await axios.get(`https://food-delivery-gj0r.onrender.com/api/users/email/${encodeURIComponent(email)}`);
        setUser(res.data);
      } catch (err) {
        console.error('Failed to fetch user:', err);
      }
    };

    fetchUser();
  }, [email]);

  const handleEdit = () => setEditing(true);

  const handleSave = () => {
    axios.put('https://food-delivery-gj0r.onrender.com/api/users', user)
      .then(res => {
        setUser(res.data);
        setEditing(false);
      })
      .catch(console.error);
  };

  const handlePictureChange = () => {
    if (!image) return;

    const formData = new FormData();
    formData.append('email', user.email);
    formData.append('profilePic', image);

    axios.post('https://food-delivery-gj0r.onrender.com/api/users/upload', formData)
      .then(res => {
        setUser(res.data);
        setPreview(null);
        setImage(null);
      })
      .catch(console.error);
  };

  const fetchOrders = () => {
    axios.get(`https://food-delivery-gj0r.onrender.com/api/orders/${email}`)
      .then(res => setOrders(res.data))
      .catch(console.error);
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    setImage(file);
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => setPreview(reader.result);
      reader.readAsDataURL(file);
    } else {
      setPreview(null);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('user');
    alert('Logged out successfully!');
    // Redirect to login page if needed
  };

  return (
    <div style={{ maxWidth: '600px', margin: '50px auto', padding: '30px', boxShadow: '0 0 10px rgba(0,0,0,0.2)', borderRadius: '10px', backgroundColor: '#fff' }}>
      <div style={{ textAlign: 'center' }}>
        <img
          src={
            preview
              ? preview
              : user.profilePic
              ? `https://food-delivery-gj0r.onrender.com${user.profilePic}`
              : 'https://via.placeholder.com/120?text=User'
          }
          alt="Profile"
          style={{
            borderRadius: '50%',
            width: '120px',
            height: '120px',
            objectFit: 'cover',
            marginBottom: '10px',
            border: '3px solid #6f42c1'
          }}
        />
        <h3 style={{ marginBottom: '20px' }}>{user.name || 'Your Name'}</h3>
        <input type="file" accept="image/*" onChange={handleImageChange} />
        <br />
        <button
          onClick={handlePictureChange}
          disabled={!image}
          style={{
            marginTop: '10px',
            background: 'none',
            border: 'none',
            color: '#007bff',
            textDecoration: 'underline',
            cursor: 'pointer'
          }}
        >
          {image ? 'Upload Picture' : 'Choose Picture'}
        </button>
      </div>

      <div style={{ marginTop: '20px' }}>
        <label>Name</label>
        <input
          type="text"
          value={user.name}
          disabled={!editing}
          onChange={(e) => setUser({ ...user, name: e.target.value })}
          style={{
            width: '100%',
            padding: '10px',
            margin: '5px 0 15px',
            border: '1px solid #ccc',
            borderRadius: '5px'
          }}
        />

        <label>Email</label>
        <input
          type="email"
          value={user.email}
          disabled
          style={{
            width: '100%',
            padding: '10px',
            margin: '5px 0 15px',
            border: '1px solid #ccc',
            borderRadius: '5px',
            backgroundColor: '#f9f9f9'
          }}
        />

        <label>Phone</label>
        <input
          type="text"
          value={user.phone}
          disabled={!editing}
          onChange={(e) => setUser({ ...user, phone: e.target.value })}
          style={{
            width: '100%',
            padding: '10px',
            margin: '5px 0 15px',
            border: '1px solid #ccc',
            borderRadius: '5px'
          }}
        />
      </div>

      <div style={{ display: 'flex', justifyContent: 'space-between', marginTop: '20px' }}>
        {!editing ? (
          <button onClick={handleEdit} style={{ padding: '10px 20px', backgroundColor: '#007bff', color: '#fff', border: 'none', borderRadius: '5px' }}>
            Edit
          </button>
        ) : (
          <button onClick={handleSave} style={{ padding: '10px 20px', backgroundColor: '#28a745', color: '#fff', border: 'none', borderRadius: '5px' }}>
            Save
          </button>
        )}
        <button onClick={fetchOrders} style={{ padding: '10px 20px', backgroundColor: '#17a2b8', color: '#fff', border: 'none', borderRadius: '5px' }}>
          See Orders
        </button>
        <button onClick={handleLogout} style={{ padding: '10px 20px', backgroundColor: '#dc3545', color: '#fff', border: 'none', borderRadius: '5px' }}>
          Logout
        </button>
      </div>

      {orders.length > 0 && (
        <div style={{ marginTop: '30px' }}>
          <h4>Past Orders</h4>
          <ul style={{ listStyleType: 'none', padding: 0 }}>
            {orders.map((order, i) => (
              <li key={i} style={{ background: '#f8f9fa', padding: '10px', margin: '5px 0', borderRadius: '5px' }}>
                <strong>{order.items.join(', ')}</strong> â€“ {new Date(order.date).toLocaleDateString()}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default UserProfile;
