import React from 'react';
import CarouselDiningout from '../carousel/CarouselDining';
import DiningoutRestaurantList from '../restaurant/DiningoutRestuarant';

const NightlifeContent = ({ showFilters2, isActive }) => {
  const styles = {
    tabContent: {
      padding: '20px',
      borderRadius: '8px',
      transition: 'all 0.5s ease-in-out',
      background: 'linear-gradient(135deg, #2c3e50, #34495e)', // Dark gradient
      color: '#ecf0f1',
    },
    activeTabContent: {
      opacity: 1,
      transform: 'scale(1)',
    },
    title: {
      fontSize: '2.5rem',
      textAlign: 'center',
      color: '#f39c12', // Vibrant yellow
      textShadow: '2px 2px 8px rgba(0, 0, 0, 0.3)', // Text shadow for effect
    },
    filtersContainer: {
      background: 'rgba(0, 0, 0, 0.1)',
      borderRadius: '8px',
      padding: '15px',
      marginTop: '20px',
    },
    filterItem: {
      margin: '8px 0',
      display: 'flex',
      alignItems: 'center',
      fontSize: '1.1rem',
    },
    filterIcon: {
      marginRight: '8px',
      fontSize: '1.5rem',
    },
    imagesContainer: {
      width: '100%',
      height: 'auto',
      borderRadius: '10px',
      transition: 'transform 0.3s ease-in-out',
    },
    imageHover: {
      transform: 'scale(1.05)', // Slight zoom effect on hover
    },
    nightlifeImage1: {
      filter: 'brightness(80%) sepia(0.5)', // Darker and sepia-tinted for nightlife vibe
    },
    nightlifeImage: {
      filter: 'brightness(70%) sepia(0.3)', // Slightly more muted for nightlife feel
    },
  };

  return (
    <div
      className="tab-content nightlife"
      style={{
        ...styles.tabContent,
        ...(isActive ? styles.activeTabContent : {}),
      }}
    >
      <h2 style={styles.title}>Nightlife Collections</h2>
      <CarouselDiningout />

      {showFilters2 && (
        <div style={styles.filtersContainer}>
          <div style={styles.filterItem}>
            <span style={styles.filterIcon}>üîç</span>
            <span>Filters</span>
          </div>
          <div style={styles.filterItem}>
            <span>Offers</span>
          </div>
          <div style={styles.filterItem}>
            <span>Rating: 4.5+</span>
          </div>
          <div style={styles.filterItem}>
            <span>Pet Friendly</span>
          </div>
          <div style={styles.filterItem}>
            <span>Outdoor Seating</span>
          </div>
          <div style={styles.filterItem}>
            <span>Serves Alcohol</span>
          </div>
          <div style={styles.filterItem}>
            <span>Open Now</span>
          </div>
        </div>
      )}

      <div style={styles.imagesContainer}>
        <img
          src="https://res.cloudinary.com/de79vmsoa/image/upload/v1744455047/ao_stx5m8.jpg"
          alt="Nightlife Bar"
          style={styles.nightlifeImage1}
        />
        <img
          src="https://res.cloudinary.com/de79vmsoa/image/upload/v1744456635/add2_ovxwkf.jpg"
          alt="Cozy Nightlife Spot"
          style={styles.nightlifeImage}
        />
      </div>

      <DiningoutRestaurantList />
    </div>
  );
};

export default NightlifeContent;
